import '../styles/employerReg.css';
import { useState } from 'react';
import { SubmitHandler, useForm, Controller } from 'react-hook-form';
import { Link } from 'react-router-dom';
import Select, { SingleValue } from 'react-select';
import { countryCodeOptions, industryOptions, numberOfEmployeeOptions } from '../utils/utilsOptions';
import { IndustryOptionType, EmployeeNumberOptionsType, CountryCodeOptionsType } from '../utils/utilsOptions'; // muszaly külön, név szerint importálni
import { fetchData, employerRegApi, apiKey, employerEmailCheckApi } from '../utils/utilsFetch'

interface formData {
    companyName: string;
    companyEmail: string;
    addressZip: string;
    addressCity: string;
    address: string;
    taxNumber: string;
    industryId: string;
    numberOfEmployees: string;
    contactPersonFirstName: string;
    contactPersonLastName: string;
    countryCode: string;
    phoneNumber: string;
    contact_person_position: string | number;
    terms_and_conditions: boolean;
    data_protection_terms: boolean;
    subscribes_to_newsletter: boolean;
};


/* Employer registration from */
const EmployerReg = () => {
    const { control, register, handleSubmit, formState: { errors }, getValues, setError, clearErrors, setValue } = useForm<formData>();
    const [message, setMessage] = useState<string>("");
    const [formData, setFormData] = useState({});
    const [currentSection, setCurrentSection] = useState(1);

    const onSubmit: SubmitHandler<formData> = async (data) => {
        //setFormData({ ...formData, ...data }); // Save form data

        if (currentSection === 1) {
            setCurrentSection(currentSection + 1);
