import '../styles/employerReg.css';
import { useState } from 'react';
import { SubmitHandler, useForm } from 'react-hook-form';
import { Link } from 'react-router-dom';
import { fetchData, employerRegApi, apiKey, employerEmailCheckApi } from '../utils/utils'

interface formData {
    companyName: string;
    companyEmail: string;
    address_zip: string;
    address_city: string;
    address: string;
    tax_number: string;
    industry_id: string;
    number_of_employees: string;
    contact_person_first_name: string;
    contact_person_last_name: string;
    country_code: string;
    phone_number: string;
    contact_person_position: string | number;
    terms_and_conditions: boolean;
    data_protection_terms: boolean;
    subscribes_to_newsletter: boolean;
};


/* Employer registration from */
