            <div className="border rounded p-3 mx-auto employerinnerreg-container">
                <div className="margin-custom">
                    <Link to='/'>
                        <img src="images/abovoLogo.svg" alt="Abovo logo" id="abovoLogo" />
                    </Link>
                </div>

                <h1 className="preregistration-form-title"> Cégadatok: </h1>

                <form onSubmit={handleSubmit(onSubmit)}>

                    <div className="form-group">
                        <label htmlFor="companyName"> Társaság megnevezése </label>
                        <input
                            type="text"
                            id="companyName"
                            placeholder='Xy kft.'
                            {...register("companyName", { 
                                required: "A cég neve nem lehet üres.",
                                maxLength: {
                                    value: 255,
                                    message: "A cég neve lehet 255 karakternél hosszabb."
                                }
