import { useForm, Controller } from 'react-hook-form';
import Select, { SingleValue } from 'react-select';
import { CountryCodeOptionsType, countryCodeOptions } from '../utils/utilsOptions'; // Ensure this import is correct based on your directory structure
import '../styles/contactUs.css';
import Footer from "./Footer";
import Navbar from "./Navbar";

interface FormData {
    lastName: string;
    firstName: string;
    email: string;
    phoneNumber: string;
    countryCode: string;
    msgText: string;
    subscribesToNewsletter: boolean;
    dataProtectionTerms: boolean;
};

function ContactUs() {
    const { register, handleSubmit, control, formState: { errors } } = useForm<FormData>();

    const onSubmit = (data: FormData) => {
        console.log(data);
    };
    return (
        <>
            <Navbar />

                <div className="row justify-content-between mx-3 mb-5 pd-5 contactUsPaddingTop">
                    <div className="row col-md-6 contactusContainer" id="contactusContainer">
                        <div className="row ">
                            <div id="getintouch" className="text-section mx-xl-auto mt-4 mb-5 row text-start">
                                <h1 id="getintouchTitle">Vedd fel velünk a kapcsolatot!</h1>
                                <p id="getintouchText">Ha kérdésed van az Abovóval kapcsolatban, fordulj hozzánk bizalommal</p>
                            </div>
                        </div>

                        <div id="contactDataOuterContainer">

                            <div id="contactDataIndividualContainer">
                                <h2 className="contactDataTitle">Általános ügyfélszolgálat</h2>
                                <p className="contactDataText">
                                    ABOVO Magyarország<br />
                                    Markó utca 7. félemelet 2.<br />
                                    Budapest<br />
                                    1055
                                </p>
                                <p className="contactDataEmail">
                                    abovo@opticruiter.com
                                </p>
                            </div>

                            <div id="contactDataIndividualContainer">
                                <h2 className="contactDataTitle">Sajtó és PR</h2>
                                <p className="contactDataText">
                                    BrekaCom<br />
                                    Bódis Réka<br />
                                    +36 30 475 0368 <br />
                                    bodis.reka@brekacom.com
                                </p>
                                <p className="contactDataEmail">
                                    abovo@opticruiter.com
                                </p>
                            </div>

                            <div id="contactDataIndividualContainer">
                                <h2 className="contactDataTitle">Fejlesztés és adatvédelem</h2>
                                <p className="contactDataEmail">
                                    development@opticruiter.com
                                </p>
                            </div>

                            <div id="contactDataIndividualContainer">
                                <h2 className="contactDataTitle">Befektetői kapcsolatok</h2>
                                <p className="contactDataEmail">
                                    investor.relations@opticruiter.com
                                </p>
                            </div>

                        </div>
                    </div>

                    <div className="col-md-6 contactusContainer" id="contactusContainer">

                        <div id="contactusSection" className="mx-3 flex-shrink-0 mb-5 mx-auto mt-4 mb-auto">

                            <form onSubmit={handleSubmit(onSubmit)}>

                                <div className="row mb-1 m-1">
                                    <div className="col-md-6">
                                        <input
                                            {...register("lastName", { required: "Last name is required." })}
                                            type="text"
                                            className={`form-control ${errors.lastName ? 'is-invalid' : ''}`}
                                            placeholder="Vezetéknév"
                                        />
                                        {errors.lastName && <div className="invalid-feedback">{errors.lastName.message}</div>}
                                    </div>
                                    <div className="col-md-6" >
                                        <input
                                            {...register("firstName", { required: "First name is required." })}
                                            type="text"
                                            className={`form-control ${errors.firstName ? 'is-invalid' : ''}`}
                                            placeholder="Keresztnév"
                                        />
                                        {errors.firstName && <div className="invalid-feedback">{errors.firstName.message}</div>}
                                    </div>
                                </div>


                                <div className="form-group m-2 p-2">
                                    <input
                                        {...register("email", { required: "Email is required." })}
                                        type="email"
                                        className={`form-control ${errors.email ? 'is-invalid' : ''}`}
                                        placeholder="E-mail cím"
                                    />
                                    {errors.email && <div className="invalid-feedback">{errors.email.message}</div>}
                                </div>





                            <div className="row mb-1 m-1">

                                    <div className="col-md-4">
                                        <Controller
                                            name="countryCode"
                                            control={control}
                                            rules={{ required: 'This field is required' }}
                                            render={({ field: { onChange, onBlur, value, ref } }) => (
                                                <Select<CountryCodeOptionsType>
                                                    options={countryCodeOptions}
                                                    placeholder="Ország"
                                                    isClearable
                                                    isSearchable
                                                    onBlur={onBlur}
                                                    ref={ref}
                                                    value={countryCodeOptions.find(countryCodeOption => countryCodeOption.value === value)}
                                                    onChange={(selectedOption: SingleValue<CountryCodeOptionsType>) => {
                                                        onChange(selectedOption ? selectedOption.value : '')
                                                    }}
                                                    formatOptionLabel={(option, { context }) => context === 'value' ? `+${option.value}` : option.label}
                                                    styles={{
                                                        container: (provided) => ({
                                                            ...provided,
                                                            width: '100%',
                                                        }),
                                                        control: (provided) => ({
