import { useForm, Controller } from 'react-hook-form';
import Select, { SingleValue } from 'react-select';
import { useState } from 'react';
import { CountryCodeOptionsType, countryCodeOptions } from '../utils/utilsOptions'; // Ensure this import is correct based on your directory structure
import '../styles/contactUs.css';
import Footer from "./Footer";
import Navbar from "./Navbar";
import { Link } from 'react-router-dom';
import { apiKey } from '../utils/utilsFetch';

interface FormData {
    lastName: string;
    firstName: string;
    email: string;
    phoneNumber: string;
    countryCode: string;
    msgText: string;
    subscribesToNewsletter: boolean;
    dataProtectionTerms: boolean;
};

function ContactUs() {
    const { register, handleSubmit, control, formState: { errors } } = useForm<FormData>();
    const [success, setSuccess] = useState(false);
    const [isFocused, setIsFocused] = useState(false);
    const [msgText, setMsgText] = useState('');



    const onSubmit = (data: FormData) => {
        const body = {
            apikey: apiKey, 
            first_name: data.firstName,
            last_name: data.lastName,
            email: data.email,
            country_code: data.countryCode,
            phone_
        }
    };


    return (
        <>

            {!success && (
                <>
